const productos = [
    { nombre: "Yerba", precio: 500, id: 1, img: "./img/yerba.jpg", idCat: "1" },
    { nombre: "Fideos", precio: 180, id: 2, img: "./img/fideos.jpg", idCat: "1" },
    { nombre: "Arroz", precio: 200, id: 3, img: "./img/arroz.jpg", idCat: "2" },
    { nombre: "Aceite", precio: 900, id: 4, img: "./img/aceite.jpg", idCat: "2" }
]

export const getProductos = () => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(productos);
        }, 2000)
    })
}

export const getProducto = (id) => {
    return new Promise(resolve => {
        setTimeout(() => {
            const producto = productos.find(prod => prod.id === id);
            resolve(producto);
        }, 2000)
    })
}

export const getProductosPorCategoria = (idCategoria) => {
    return new Promise( resolve => {
        setTimeout( ()=> {
            const getProductosPorCategoria = productos.filter(prod => prod.idCat === idCategoria);
            resolve(getProductosPorCategoria);
        }, 100)
    })
}
